import React from 'react';

class ResultDetails extends React.Component {

    render() {
        const { isbn, title, author, imageUrl, description } = props.location.state;
        return (
            <div className="book">
                <h1>{this.title}</h1>
                <h2>{this.author}</h2>
                <span>{this.isbn}</span>
                <img src={this.imageUrl} alt="book cover" />
                <p> {this.description} </p>
            </div>
        )
    }

}

export default ResultDetails;
