import React from 'react';

class SearchForm extends React.Component {
  render() {
    return (
      <form className="search-form">
        <h1>BOOK SEARCH</h1>
        <input type="text" placeholder="Search here"/>
        <span> Search by: </span>
        <select>
          <option> Title </option>
          <option> Author </option>
          <option> ISBN </option>
        </select>
      </form>
    )
  }

}

export default SearchForm;
