import React from 'react';
import SearchForm from './SearchForm';
import SearchResults from './SearchResults';
import API from '../services/API';

class App extends React.Component {
  state = {
    searchresults: []
  }

  searchByIsbn = async (query) => {
    try {
      const response = await API.makeRequest({ url: 'api/auth/token', method: 'POST', data: this.state });
      alert('logged in');
      window.localStorage.setItem("token", response.token);
      this.setState({ redirectToChat: true });
    } catch (error) {
      alert(error);
    }
  }

  searchForBooks = (query, type) => {
    if (type === 'isbn') {
      this.searchByIsbn(query);
    }

  }

  render() {
    return (
      <React.Fragment>
        <SearchForm searchForBooks={this.searchForBooks} />
        <SearchResults />
      </React.Fragment>

    )
  }

}

export default App;
