import React from 'react';
import SearchForm from './SearchForm';
import SearchResults from './SearchResults';
import API from '../services/API';

class App extends React.Component {
  state = {
    searchresults: []
  }

  searchByIsbn = async (query) => {
    try {
      const response = await API.makeRequest({ parameters: 'api/autbibkeys=ISBN:{query}', method: 'GET', data: this.state.searchresults });
      alert('searched');
    } catch (error) {
      alert(error);
    }
  }

  searchForBooks = (query, type) => {
    if (type === 'isbn') {
      this.searchByIsbn(query);
    }

  }

  render() {
    return (
      <React.Fragment>
        <SearchForm searchForBooks={this.searchForBooks} />
        <SearchResults />
      </React.Fragment>

    )
  }

}

export default App;
