import React from 'react';

class ResultDetails extends React.Component {

    render() {
        const { isbn, title, author, imageUrl, description } = this.props.location.state;
        return (
            <div className="book">
                <h1>{title}</h1>
                <h2>{author}</h2>
                <span>{isbn}</span>
                <img src={imageUrl} alt="book cover" />
                <p> {description} </p>
            </div>
        )
    }

}

export default ResultDetails;
